function createUpgrade(id, x, y, name, tooltip, cost, callback, formula, category, tooltip_value) {
   var self = {
		id : id,
		name: name,
		x: x,
		y: y,
		cost: cost,
		tooltip: tooltip,
		callback: callback,
		formula: formula,
		bought: false,
		available: false,
		
		buy: function(){
			if (window.gold >= this.cost && !this.bought) {	
				window.gold -= this.cost;
				this.bought = true;
				this.callback();
				updateUpgrades();
				hideTooltip();
			}
		}
  };
  if (typeof category != 'undefined') {self.category = category}
  if (tooltip_value) {self.tooltip_value = tooltip_value}
  return self;
}

