function htmlUpgrade(id) {
	var upgrade = upgrade_defs[id];
	var upgrade_x = -upgrade.x * 48;
	var upgrade_y = -upgrade.y * 48;
	var upgrade_tooltip_value = ""
	
	if (upgrade.tooltip_value) {upgrade_tooltip_value = "<br/>(" +upgrade.tooltip_value() + ")"}
	
	var upgrade_element = $("<div></div>");
	upgrade_element.attr("id", upgrade.id);
	upgrade_element.attr("class", "upgrade");
	upgrade_element.attr("onmouseleave", "hideTooltip()");
	upgrade_element.attr("onclick", "upgrade_defs["+id+"].buy();console.log('clicked')");
	upgrade_element.attr("onmouseover", "showTooltip(this.id, 'url(images/upgrade_sheet.png)"+upgrade_x+"px "+upgrade_y+"px', '"+upgrade.name+"', '"+upgrade.tooltip+upgrade_tooltip_value+"')");
	upgrade_element.attr("style", "float:left;height:48px;width:48px;background:url(images/upgrade_sheet.png) "+upgrade_x+"px "+upgrade_y+"px;");
	
	if (upgrade.cost > gold) {upgrade_element.css('filter','grayscale(100%)');}
	//if (upgrade.cost > gold) {upgrade_element.css('float','right)')}
	//else {upgrade_element.css('float','left)')}
	$("#leftContent").append(upgrade_element);
	
	//blueprints_bought++;
}

function displayUpgrade(id) {
	var upgrade = upgrade_defs[id];
	var upgrade_x = -upgrade.x * 48;
	var upgrade_y = -upgrade.y * 48;
	var upgrade_tooltip_value = ""
	
	if (upgrade.tooltip_value) {upgrade_tooltip_value = "<br/>(" +upgrade.tooltip_value() + ")"}
	
	var upgrade_element = $("<div></div>");
	upgrade_element.attr("id", upgrade.id);
	upgrade_element.attr("class", "upgrade");
	upgrade_element.attr("onmouseleave", "hideTooltip()");
	upgrade_element.attr("onmouseover", "showTooltip(this.id, 'url(images/upgrade_sheet.png)"+upgrade_x+"px "+upgrade_y+"px', '"+upgrade.name+"', '"+upgrade.tooltip+upgrade_tooltip_value+"')");
	upgrade_element.attr("style", "float:left;height:48px;width:48px;background:url(images/upgrade_sheet.png) "+upgrade_x+"px "+upgrade_y+"px;");
	
	$("#upgrade_list").append(upgrade_element);

}

function updateBoughtUpgrades() {
	$("#upgrades_menu").find(".upgrade").each(function(i, obj) {
		obj.remove();
	});

	for (i = 0; i < upgrade_defs.length; i++) {
		if (upgrade_defs[i].category == upgrade_category && upgrade_defs[i].bought) {
			displayUpgrade(i);
		}
	}
}

function updateUpgrades() {
	hideUpgrades();


	for (var i = 0; i < upgrade_defs.length; i++) {
		var upgrade = upgrade_defs[i];
		if (upgrade.available && !upgrade.bought && upgrade.cost < gold) {
			htmlUpgrade(i);
		}
	}	
	for (var i = 0; i < upgrade_defs.length; i++) {
		var upgrade = upgrade_defs[i];
		if (upgrade.available && !upgrade.bought && upgrade.cost >= gold) {
			htmlUpgrade(i);
		}
	}
	
	if (special.black_hole.karma_points >= 1) {$("#karma_upgrade").show();}
	else {$("#karma_upgrade").hide();}
}

function hideUpgrades() {
	$("#leftContent").find(".upgrade").each(function(i, obj) {
		if (obj.id != "karma_upgrade" && obj.id != "blueprint_upgrade" && obj.id != "tick_upgrade" && obj.id != "sacrifice_reset_menu" && obj.id != "automation_upgrade" && obj.id != "automation_menu" && obj.id != "temporal_upgrade" && obj.id != "temporal_menu" && obj.id != "trade_upgrade" && obj.id != "trade_menu") {
			obj.remove();
		}
	});
}

function autoUpgrade() {
	if (special.automation.autoupgrade) {
		for (var i = 0; i < upgrade_defs.length; i++) {
			var upgrade = upgrade_defs[i];
			if (!upgrade.bought && upgrade.available) {
				upgrade.buy();
			}
		}
	}
}